import React, { useState, useEffect } from 'react';
import { collection, addDoc, getDocs, doc, updateDoc, deleteDoc } from 'firebase/firestore';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import { db, storage } from '../../firebase/config';
import { toast } from 'react-toastify';

const AdminCars = () => {
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddForm, setShowAddForm] = useState(false);
  const [carImage, setCarImage] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    type: '',
    pricePerKm: '',
    isAvailable: true,
    features: ''
  });
  const [editingId, setEditingId] = useState(null);

  // Fetch cars on component mount
  useEffect(() => {
    fetchCars();
  }, []);

  const fetchCars = async () => {
    try {
      setLoading(true);
      const carsSnapshot = await getDocs(collection(db, 'cars'));
      const carsList = carsSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setCars(carsList);
    } catch (error) {
      toast.error('Error fetching cars: ' + error.message);
      console.error('Error fetching cars:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  const handleImageChange = (e) => {
    if (e.target.files[0]) {
      setCarImage(e.target.files[0]);
    }
  };

  const handleAddCar = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);

      // Validate form data
      if (!formData.name || !formData.type || !formData.pricePerKm || !carImage) {
        toast.error('Please fill all required fields and upload an image');
        return;
      }

      // Upload image to Firebase Storage
      const imageRef = ref(storage, `cars/${Date.now()}-${carImage.name}`);
      await uploadBytes(imageRef, carImage);
      const imageUrl = await getDownloadURL(imageRef);

      // Prepare car data
      const carData = {
        ...formData,
        pricePerKm: parseFloat(formData.pricePerKm),
        imageUrl,
        features: formData.features.split(',').map(feature => feature.trim()),
        createdAt: new Date().toISOString()
      };

      // Add to Firestore
      await addDoc(collection(db, 'cars'), carData);

      // Reset form
      setFormData({
        name: '',
        type: '',
        pricePerKm: '',
        isAvailable: true,
        features: ''
      });
      setCarImage(null);
      setShowAddForm(false);

      // Refresh car list
      fetchCars();
      toast.success('Car added successfully');
    } catch (error) {
      toast.error('Error adding car: ' + error.message);
      console.error('Error adding car:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleEditCar = (car) => {
    setEditingId(car.id);
    setFormData({
      name: car.name,
      type: car.type,
      pricePerKm: car.pricePerKm.toString(),
      isAvailable: car.isAvailable,
      features: car.features ? car.features.join(', ') : ''
    });
    setShowAddForm(true);
  };

  const handleUpdateCar = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);

      // Prepare updated data
      let updatedData = {
        ...formData,
        pricePerKm: parseFloat(formData.pricePerKm),
        features: formData.features.split(',').map(feature => feature.trim()),
        updatedAt: new Date().toISOString()
      };

      // If a new image was uploaded
      if (carImage) {
        const imageRef = ref(storage, `cars/${Date.now()}-${carImage.name}`);
        await uploadBytes(imageRef, carImage);
        const imageUrl = await getDownloadURL(imageRef);
        updatedData.imageUrl = imageUrl;
      }

      // Update in Firestore
      await updateDoc(doc(db, 'cars', editingId), updatedData);

      // Reset form
      setFormData({
        name: '',
        type: '',
        pricePerKm: '',
        isAvailable: true,
        features: ''
      });
      setCarImage(null);
      setShowAddForm(false);
      setEditingId(null);

      // Refresh car list
      fetchCars();
      toast.success('Car updated successfully');
    } catch (error) {
      toast.error('Error updating car: ' + error.message);
      console.error('Error updating car:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteCar = async (id) => {
    if (window.confirm('Are you sure you want to delete this car?')) {
      try {
        setLoading(true);
        await deleteDoc(doc(db, 'cars', id));
        toast.success('Car deleted successfully');
        fetchCars();
      } catch (error) {
        toast.error('Error deleting car: ' + error.message);
        console.error('Error deleting car:', error);
      } finally {
        setLoading(false);
      }
    }
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Manage Cars</h1>
        <button
          onClick={() => {
            setShowAddForm(!showAddForm);
            setEditingId(null);
            setFormData({
              name: '',
              type: '',
              pricePerKm: '',
              isAvailable: true,
              features: ''
            });
            setCarImage(null);
          }}
          className="bg-primary text-white px-4 py-2 rounded hover:bg-primary-dark transition-colors"
        >
          {showAddForm ? 'Cancel' : 'Add New Car'}
        </button>
      </div>

      {showAddForm && (
        <div className="bg-white p-6 rounded-lg shadow-md mb-8">
          <h2 className="text-xl font-semibold mb-4">{editingId ? 'Edit Car' : 'Add New Car'}</h2>
          <form onSubmit={editingId ? handleUpdateCar : handleAddCar}>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-gray-700 mb-2">Car Name</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                  required
                />
              </div>
              <div>
                <label className="block text-gray-700 mb-2">Car Type</label>
                <select
                  name="type"
                  value={formData.type}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                  required
                >
                  <option value="">Select Type</option>
                  <option value="Sedan">Sedan</option>
                  <option value="SUV">SUV</option>
                  <option value="Hatchback">Hatchback</option>
                  <option value="Luxury">Luxury</option>
                  <option value="Van">Van</option>
                </select>
              </div>
              <div>
                <label className="block text-gray-700 mb-2">Price Per Kilometer ($)</label>
                <input
                  type="number"
                  name="pricePerKm"
                  value={formData.pricePerKm}
                  onChange={handleInputChange}
                  min="0"
                  step="0.01"
                  className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                  required
                />
              </div>
              <div>
                <label className="block text-gray-700 mb-2">Car Image</label>
                <input
                  type="file"
                  onChange={handleImageChange}
                  accept="image/*"
                  className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                  required={!editingId}
                />
                {editingId && !carImage && (
                  <p className="text-sm text-gray-500 mt-1">Leave empty to keep current image</p>
                )}
              </div>
              <div className="md:col-span-2">
                <label className="block text-gray-700 mb-2">Features (comma separated)</label>
                <input
                  type="text"
                  name="features"
                  value={formData.features}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                  placeholder="AC, GPS, Bluetooth, etc."
                />
              </div>
              <div className="md:col-span-2">
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    name="isAvailable"
                    checked={formData.isAvailable}
                    onChange={handleInputChange}
                    className="h-4 w-4 text-primary focus:ring-primary border-gray-300 rounded"
                  />
                  <span>Available for Booking</span>
                </label>
              </div>
            </div>
            <div className="mt-6">
              <button
                type="submit"
                className="bg-primary text-white px-4 py-2 rounded hover:bg-primary-dark transition-colors"
                disabled={loading}
              >
                {loading ? 'Processing...' : editingId ? 'Update Car' : 'Add Car'}
              </button>
            </div>
          </form>
        </div>
      )}

      {loading && !showAddForm ? (
        <div className="flex justify-center py-12">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary"></div>
        </div>
      ) : (
        <div className="bg-white rounded-lg shadow-md overflow-hidden">
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Image</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Car Details</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Price</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {cars.length === 0 ? (
                  <tr>
                    <td colSpan="5" className="px-6 py-4 text-center text-sm text-gray-500">
                      No cars found. Add your first car!
                    </td>
                  </tr>
                ) : (
                  cars.map(car => (
                    <tr key={car.id}>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <img src={car.imageUrl} alt={car.name} className="h-16 w-24 object-cover rounded" />
                      </td>
                      <td className="px-6 py-4">
                        <div className="text-sm font-medium text-gray-900">{car.name}</div>
                        <div className="text-sm text-gray-500">{car.type}</div>
                        <div className="text-xs text-gray-500 mt-1">
                          {car.features && car.features.join(', ')}
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">${car.pricePerKm} per km</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${car.isAvailable ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>
                          {car.isAvailable ? 'Available' : 'Unavailable'}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                        <button
                          onClick={() => handleEditCar(car)}
                          className="text-primary hover:text-primary-dark mr-3"
                        >
                          Edit
                        </button>
                        <button
                          onClick={() => handleDeleteCar(car.id)}
                          className="text-red-600 hover:text-red-900"
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminCars;
