import React, { useState, useEffect } from 'react';
import { collection, query, where, getDocs, addDoc, doc, updateDoc, deleteDoc } from 'firebase/firestore';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { db, storage, auth } from '../../firebase/config';
import { toast } from 'react-toastify';

const AdminDrivers = () => {
  const [drivers, setDrivers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddForm, setShowAddForm] = useState(false);
  const [driverImage, setDriverImage] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    licenseNumber: '',
    isAvailable: true,
    password: '',
    address: ''
  });
  const [editingId, setEditingId] = useState(null);

  // Fetch drivers on component mount
  useEffect(() => {
    fetchDrivers();
  }, []);

  const fetchDrivers = async () => {
    try {
      setLoading(true);

      // Query users with role 'driver'
      const driversQuery = query(
        collection(db, 'users'),
        where('role', '==', 'driver')
      );

      const driversSnapshot = await getDocs(driversQuery);
      const driversList = driversSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));

      setDrivers(driversList);
    } catch (error) {
      toast.error('Error fetching drivers: ' + error.message);
      console.error('Error fetching drivers:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  const handleImageChange = (e) => {
    if (e.target.files[0]) {
      setDriverImage(e.target.files[0]);
    }
  };

  const handleAddDriver = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);

      // Validate form data
      if (!formData.name || !formData.email || !formData.phone || !formData.licenseNumber || !formData.password || !driverImage) {
        toast.error('Please fill all required fields and upload an image');
        return;
      }

      // Upload image to Firebase Storage
      const imageRef = ref(storage, `drivers/${Date.now()}-${driverImage.name}`);
      await uploadBytes(imageRef, driverImage);
      const imageUrl = await getDownloadURL(imageRef);

      // Create the user in Firebase Auth
      const userCredential = await createUserWithEmailAndPassword(auth, formData.email, formData.password);
      const uid = userCredential.user.uid;

      // Prepare driver data
      const driverData = {
        name: formData.name,
        email: formData.email,
        phone: formData.phone,
        licenseNumber: formData.licenseNumber,
        address: formData.address,
        isAvailable: formData.isAvailable,
        imageUrl,
        role: 'driver',
        createdAt: new Date().toISOString(),
        createdBy: 'admin'
      };

      // Add to Firestore
      await addDoc(collection(db, 'users'), {
        ...driverData,
        uid
      });

      // Reset form
      setFormData({
        name: '',
        email: '',
        phone: '',
        licenseNumber: '',
        isAvailable: true,
        password: '',
        address: ''
      });
      setDriverImage(null);
      setShowAddForm(false);

      // Refresh driver list
      fetchDrivers();
      toast.success('Driver added successfully');
    } catch (error) {
      let errorMessage = 'Error adding driver';

      if (error.code === 'auth/email-already-in-use') {
        errorMessage = 'Email is already in use';
      } else if (error.code === 'auth/invalid-email') {
        errorMessage = 'Invalid email format';
      } else if (error.code === 'auth/weak-password') {
        errorMessage = 'Password is too weak';
      }

      toast.error(errorMessage);
      console.error('Error adding driver:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleEditDriver = (driver) => {
    setEditingId(driver.id);
    setFormData({
      name: driver.name,
      email: driver.email,
      phone: driver.phone || '',
      licenseNumber: driver.licenseNumber || '',
      isAvailable: driver.isAvailable !== false,
      password: '', // Password field is empty during edit
      address: driver.address || ''
    });
    setShowAddForm(true);
  };

  const handleUpdateDriver = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);

      // Prepare updated data
      let updatedData = {
        name: formData.name,
        phone: formData.phone,
        licenseNumber: formData.licenseNumber,
        address: formData.address,
        isAvailable: formData.isAvailable,
        updatedAt: new Date().toISOString()
      };

      // If a new image was uploaded
      if (driverImage) {
        const imageRef = ref(storage, `drivers/${Date.now()}-${driverImage.name}`);
        await uploadBytes(imageRef, driverImage);
        const imageUrl = await getDownloadURL(imageRef);
        updatedData.imageUrl = imageUrl;
      }

      // Update in Firestore
      await updateDoc(doc(db, 'users', editingId), updatedData);

      // Reset form
      setFormData({
        name: '',
        email: '',
        phone: '',
        licenseNumber: '',
        isAvailable: true,
        password: '',
        address: ''
      });
      setDriverImage(null);
      setShowAddForm(false);
      setEditingId(null);

      // Refresh driver list
      fetchDrivers();
      toast.success('Driver updated successfully');
    } catch (error) {
      toast.error('Error updating driver: ' + error.message);
      console.error('Error updating driver:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteDriver = async (id) => {
    if (window.confirm('Are you sure you want to delete this driver?')) {
      try {
        setLoading(true);
        await deleteDoc(doc(db, 'users', id));
        toast.success('Driver deleted successfully');
        fetchDrivers();
      } catch (error) {
        toast.error('Error deleting driver: ' + error.message);
        console.error('Error deleting driver:', error);
      } finally {
        setLoading(false);
      }
    }
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Manage Drivers</h1>
        <button
          onClick={() => {
            setShowAddForm(!showAddForm);
            setEditingId(null);
            setFormData({
              name: '',
              email: '',
              phone: '',
              licenseNumber: '',
              isAvailable: true,
              password: '',
              address: ''
            });
            setDriverImage(null);
          }}
          className="bg-primary text-white px-4 py-2 rounded hover:bg-primary-dark transition-colors"
        >
          {showAddForm ? 'Cancel' : 'Add New Driver'}
        </button>
      </div>

      {showAddForm && (
        <div className="bg-white p-6 rounded-lg shadow-md mb-8">
          <h2 className="text-xl font-semibold mb-4">{editingId ? 'Edit Driver' : 'Add New Driver'}</h2>
          <form onSubmit={editingId ? handleUpdateDriver : handleAddDriver}>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-gray-700 mb-2">Full Name</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                  required
                />
              </div>
              <div>
                <label className="block text-gray-700 mb-2">Email</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                  required
                  disabled={editingId} // Email cannot be changed for existing driver
                />
                {editingId && (
                  <p className="text-xs text-gray-500 mt-1">Email cannot be changed</p>
                )}
              </div>
              <div>
                <label className="block text-gray-700 mb-2">Phone Number</label>
                <input
                  type="tel"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                  required
                />
              </div>
              <div>
                <label className="block text-gray-700 mb-2">Driver License Number</label>
                <input
                  type="text"
                  name="licenseNumber"
                  value={formData.licenseNumber}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                  required
                />
              </div>
              <div>
                <label className="block text-gray-700 mb-2">Address</label>
                <input
                  type="text"
                  name="address"
                  value={formData.address}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                />
              </div>
              <div>
                <label className="block text-gray-700 mb-2">Profile Image</label>
                <input
                  type="file"
                  onChange={handleImageChange}
                  accept="image/*"
                  className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                  required={!editingId}
                />
                {editingId && !driverImage && (
                  <p className="text-sm text-gray-500 mt-1">Leave empty to keep current image</p>
                )}
              </div>
              {!editingId && (
                <div>
                  <label className="block text-gray-700 mb-2">Password</label>
                  <input
                    type="password"
                    name="password"
                    value={formData.password}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                    required={!editingId}
                  />
                </div>
              )}
              <div className="md:col-span-2">
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    name="isAvailable"
                    checked={formData.isAvailable}
                    onChange={handleInputChange}
                    className="h-4 w-4 text-primary focus:ring-primary border-gray-300 rounded"
                  />
                  <span>Available for Assignments</span>
                </label>
              </div>
            </div>
            <div className="mt-6">
              <button
                type="submit"
                className="bg-primary text-white px-4 py-2 rounded hover:bg-primary-dark transition-colors"
                disabled={loading}
              >
                {loading ? 'Processing...' : editingId ? 'Update Driver' : 'Add Driver'}
              </button>
            </div>
          </form>
        </div>
      )}

      {loading && !showAddForm ? (
        <div className="flex justify-center py-12">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary"></div>
        </div>
      ) : (
        <div className="bg-white rounded-lg shadow-md overflow-hidden">
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Driver</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Contact</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">License</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {drivers.length === 0 ? (
                  <tr>
                    <td colSpan="5" className="px-6 py-4 text-center text-sm text-gray-500">
                      No drivers found. Add your first driver!
                    </td>
                  </tr>
                ) : (
                  drivers.map(driver => (
                    <tr key={driver.id}>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="flex items-center">
                          <div className="h-10 w-10 flex-shrink-0">
                            {driver.imageUrl ? (
                              <img className="h-10 w-10 rounded-full object-cover" src={driver.imageUrl} alt={driver.name} />
                            ) : (
                              <div className="h-10 w-10 rounded-full bg-gray-200 flex items-center justify-center">
                                <span className="text-gray-500">{driver.name.charAt(0)}</span>
                              </div>
                            )}
                          </div>
                          <div className="ml-4">
                            <div className="text-sm font-medium text-gray-900">{driver.name}</div>
                            <div className="text-sm text-gray-500">Added: {new Date(driver.createdAt).toLocaleDateString()}</div>
                          </div>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm text-gray-900">{driver.email}</div>
                        <div className="text-sm text-gray-500">{driver.phone}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        {driver.licenseNumber}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${driver.isAvailable !== false ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>
                          {driver.isAvailable !== false ? 'Available' : 'Unavailable'}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                        <button
                          onClick={() => handleEditDriver(driver)}
                          className="text-primary hover:text-primary-dark mr-3"
                        >
                          Edit
                        </button>
                        <button
                          onClick={() => handleDeleteDriver(driver.id)}
                          className="text-red-600 hover:text-red-900"
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDrivers;
