import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { collection, query, orderBy, getDocs, updateDoc, doc, onSnapshot, writeBatch } from 'firebase/firestore';
import { db } from '../../firebase/config';
import { useAuth } from '../../context/AuthContext';
import { toast } from 'react-toastify';

const CustomerNotifications = () => {
  const { currentUser } = useAuth();
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [isOpen, setIsOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    if (!currentUser || !currentUser.uid) return;

    // Listen for new notifications
    const notificationsRef = collection(db, 'users', currentUser.uid, 'notifications');
    const q = query(notificationsRef, orderBy('createdAt', 'desc'));

    const unsubscribe = onSnapshot(q, (snapshot) => {
      const notificationData = [];
      let unread = 0;

      snapshot.forEach((doc) => {
        const notification = {
          id: doc.id,
          ...doc.data()
        };

        if (!notification.read) {
          unread++;
        }

        notificationData.push(notification);
      });

      setNotifications(notificationData);
      setUnreadCount(unread);

      // Show toast for new unread booking acceptance notifications
      if (unread > 0 && !isOpen) {
        const acceptedRides = notificationData.filter(n => 
          n.type === 'booking_accepted' && !n.read
        );

        if (acceptedRides.length > 0) {
          toast.success('A driver has accepted your ride request!');
        }
      }
    });

    return () => unsubscribe();
  }, [currentUser, isOpen]);

  const handleViewBooking = (bookingId) => {
    navigate(`/booking/${bookingId}`);
    setIsOpen(false);
  };

  const handleMarkAsRead = async (notificationId) => {
    try {
      await updateDoc(doc(db, 'users', currentUser.uid, 'notifications', notificationId), {
        read: true,
        readAt: new Date().toISOString()
      });
    } catch (error) {
      console.error('Error marking notification as read:', error);
    }
  };

  const handleMarkAllAsRead = async () => {
    try {
      const batch = writeBatch(db);

      notifications.forEach(notification => {
        if (!notification.read) {
          const notificationRef = doc(db, 'users', currentUser.uid, 'notifications', notification.id);
          batch.update(notificationRef, { 
            read: true,
            readAt: new Date().toISOString()
          });
        }
      });

      await batch.commit();
    } catch (error) {
      console.error('Error marking all notifications as read:', error);
    }
  };

  return (
    <div className="relative">
      {/* Notification Bell Icon */}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="relative p-2 text-gray-600 hover:text-gray-900 focus:outline-none"
      >
        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6.002 6.002 0 00-4-5.659V5a2 2 0 10-4 0v.341C7.67 6.165 6 8.388 6 11v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9" />
        </svg>

        {unreadCount > 0 && (
          <span className="absolute top-0 right-0 h-5 w-5 text-xs text-white bg-red-500 rounded-full flex items-center justify-center">
            {unreadCount}
          </span>
        )}
      </button>

      {/* Dropdown panel */}
      {isOpen && (
        <div className="absolute right-0 mt-2 w-80 bg-white rounded-md shadow-lg py-1 z-50">
          <div className="flex justify-between items-center px-4 py-2 border-b">
            <h3 className="text-sm font-medium">Notifications</h3>
            {unreadCount > 0 && (
              <button
                onClick={handleMarkAllAsRead}
                className="text-xs text-primary hover:text-primary-dark"
              >
                Mark all as read
              </button>
            )}
          </div>

          {notifications.length === 0 ? (
            <div className="px-4 py-6 text-center text-gray-500 text-sm">
              No notifications
            </div>
          ) : (
            <div className="max-h-96 overflow-y-auto">
              {notifications.map(notification => (
                <div
                  key={notification.id}
                  className={`px-4 py-3 border-b ${!notification.read ? 'bg-blue-50' : ''}`}
                >
                  <div className="flex justify-between items-start">
                    <h4 className="text-sm font-medium">{notification.title}</h4>
                    <span className="text-xs text-gray-500">
                      {new Date(notification.createdAt).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                    </span>
                  </div>
                  <p className="text-sm text-gray-600 mt-1">{notification.body}</p>

                  {notification.type === 'booking_accepted' && (
                    <div className="mt-2">
                      {notification.driverName && (
                        <p className="text-xs text-gray-600">
                          <span className="font-medium">Driver:</span> {notification.driverName}
                        </p>
                      )}
                      {notification.driverPhone && (
                        <p className="text-xs text-gray-600">
                          <span className="font-medium">Contact:</span> {notification.driverPhone}
                        </p>
                      )}
                      <div className="flex space-x-2 mt-2">
                        <button
                          onClick={() => handleViewBooking(notification.bookingId)}
                          className="text-xs bg-primary text-white px-3 py-1 rounded hover:bg-primary-dark"
                        >
                          View Details
                        </button>
                        {!notification.read && (
                          <button
                            onClick={() => handleMarkAsRead(notification.id)}
                            className="text-xs bg-gray-200 text-gray-800 px-3 py-1 rounded hover:bg-gray-300"
                          >
                            Mark as Read
                          </button>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default CustomerNotifications;
