import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { doc, getDoc, collection, query, where, getDocs, addDoc } from 'firebase/firestore';
import { db } from '../../firebase/config';
import { useAuth } from '../../context/AuthContext';
import { toast } from 'react-toastify';
import LoadingSpinner from '../../components/common/LoadingSpinner';

const BookingPage = () => {
  const { carId } = useParams();
  const { currentUser } = useAuth();
  const navigate = useNavigate();
  const mapRef = useRef(null);
  const [mapLoaded, setMapLoaded] = useState(false);
  const [map, setMap] = useState(null);
  const [pickupMarker, setPickupMarker] = useState(null);
  const [dropMarker, setDropMarker] = useState(null);

  const [car, setCar] = useState(null);
  const [availableDrivers, setAvailableDrivers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [bookingType, setBookingType] = useState('withDriver'); // 'withDriver' or 'selfDrive'
  const [formData, setFormData] = useState({
    pickupLocation: '',
    dropLocation: '',
    pickupDate: '',
    pickupTime: '',
    returnDate: '',
    returnTime: '',
    estimatedDistance: '',
    driverId: '',
    specialRequests: '',
    customerPhone: ''
  });
  const [bookingSummary, setBookingSummary] = useState({
    totalAmount: 0,
    driverCharge: 0,
    distanceCharge: 0
  });
  const [locationMode, setLocationMode] = useState('pickup'); // 'pickup' or 'drop'

  useEffect(() => {
    if (carId) {
      fetchCarDetails();
      fetchAvailableDrivers();
    }

    // Load Google Maps API script
    const googleMapsScript = document.createElement('script');
    googleMapsScript.src = `https://maps.googleapis.com/maps/api/js?key=YOUR_GOOGLE_MAPS_API_KEY&libraries=places`;
    googleMapsScript.async = true;
    googleMapsScript.defer = true;
    googleMapsScript.onload = initializeMap;
    document.head.appendChild(googleMapsScript);

    return () => {
      // Clean up the script tag when component unmounts
      const script = document.querySelector(`script[src*="maps.googleapis.com/maps/api"]`);
      if (script) {
        script.remove();
      }
    };
  }, [carId]);

  useEffect(() => {
    // Prefill customer phone if available in user profile
    if (currentUser && currentUser.phoneNumber) {
      setFormData(prev => ({
        ...prev,
        customerPhone: currentUser.phoneNumber
      }));
    }
  }, [currentUser]);

  useEffect(() => {
    // Calculate booking cost whenever relevant form data changes
    calculateBookingCost();
  }, [formData.estimatedDistance, car, bookingType, formData.driverId]);

  const initializeMap = () => {
    if (!mapRef.current) return;

    const defaultLocation = { lat: 37.7749, lng: -122.4194 }; // San Francisco as default

    const mapInstance = new window.google.maps.Map(mapRef.current, {
      center: defaultLocation,
      zoom: 13,
      mapTypeControl: false,
      streetViewControl: false,
      fullscreenControl: false,
    });

    // Initialize autocomplete for pickup location
    const pickupInput = document.getElementById('pickupLocation');
    const pickupAutocomplete = new window.google.maps.places.Autocomplete(pickupInput);
    pickupAutocomplete.addListener('place_changed', () => {
      const place = pickupAutocomplete.getPlace();
      if (!place.geometry) return;

      // Update form with selected location
      setFormData(prev => ({
        ...prev,
        pickupLocation: place.formatted_address
      }));

      // Set marker
      if (pickupMarker) pickupMarker.setMap(null);
      const marker = new window.google.maps.Marker({
        map: mapInstance,
        position: place.geometry.location,
        icon: {
          url: 'http://maps.google.com/mapfiles/ms/icons/green-dot.png'
        },
        title: 'Pickup Location'
      });
      setPickupMarker(marker);

      // Center map
      mapInstance.setCenter(place.geometry.location);
      updateEstimatedDistance();
    });

    // Initialize autocomplete for drop location
    const dropInput = document.getElementById('dropLocation');
    const dropAutocomplete = new window.google.maps.places.Autocomplete(dropInput);
    dropAutocomplete.addListener('place_changed', () => {
      const place = dropAutocomplete.getPlace();
      if (!place.geometry) return;

      // Update form with selected location
      setFormData(prev => ({
        ...prev,
        dropLocation: place.formatted_address
      }));

      // Set marker
      if (dropMarker) dropMarker.setMap(null);
      const marker = new window.google.maps.Marker({
        map: mapInstance,
        position: place.geometry.location,
        icon: {
          url: 'http://maps.google.com/mapfiles/ms/icons/red-dot.png'
        },
        title: 'Drop Location'
      });
      setDropMarker(marker);

      // Center map
      mapInstance.setCenter(place.geometry.location);
      updateEstimatedDistance();
    });

    // Allow clicking on map to set locations
    mapInstance.addListener('click', (event) => {
      const geocoder = new window.google.maps.Geocoder();
      geocoder.geocode({ 'location': event.latLng }, (results, status) => {
        if (status === 'OK' && results[0]) {
          const address = results[0].formatted_address;

          if (locationMode === 'pickup') {
            // Update pickup location
            setFormData(prev => ({
              ...prev,
              pickupLocation: address
            }));

            // Set marker
            if (pickupMarker) pickupMarker.setMap(null);
            const marker = new window.google.maps.Marker({
              map: mapInstance,
              position: event.latLng,
              icon: {
                url: 'http://maps.google.com/mapfiles/ms/icons/green-dot.png'
              },
              title: 'Pickup Location'
            });
            setPickupMarker(marker);
          } else {
            // Update drop location
            setFormData(prev => ({
              ...prev,
              dropLocation: address
            }));

            // Set marker
            if (dropMarker) dropMarker.setMap(null);
            const marker = new window.google.maps.Marker({
              map: mapInstance,
              position: event.latLng,
              icon: {
                url: 'http://maps.google.com/mapfiles/ms/icons/red-dot.png'
              },
              title: 'Drop Location'
            });
            setDropMarker(marker);
          }

          updateEstimatedDistance();
        }
      });
    });

    setMap(mapInstance);
    setMapLoaded(true);
  };

  const updateEstimatedDistance = () => {
    if (!formData.pickupLocation || !formData.dropLocation) return;

    const directionsService = new window.google.maps.DirectionsService();
    directionsService.route({
      origin: formData.pickupLocation,
      destination: formData.dropLocation,
      travelMode: 'DRIVING'
    }, (response, status) => {
      if (status === 'OK') {
        // Get distance in kilometers
        const distanceInMeters = response.routes[0].legs[0].distance.value;
        const distanceInKm = (distanceInMeters / 1000).toFixed(1);

        setFormData(prev => ({
          ...prev,
          estimatedDistance: distanceInKm
        }));

        // Optionally show the route
        const directionsRenderer = new window.google.maps.DirectionsRenderer({
          map: map,
          directions: response,
          suppressMarkers: true // We already have our own markers
        });
      }
    });
  };

  const fetchCarDetails = async () => {
    try {
      const carDoc = await getDoc(doc(db, 'cars', carId));
      if (carDoc.exists()) {
        setCar({ id: carDoc.id, ...carDoc.data() });
      } else {
        toast.error('Car not found');
        navigate('/cars');
      }
    } catch (error) {
      toast.error('Error fetching car details: ' + error.message);
      console.error('Error fetching car details:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchAvailableDrivers = async () => {
    try {
      const driversQuery = query(
        collection(db, 'users'),
        where('role', '==', 'driver'),
        where('isAvailable', '==', true)
      );

      const driversSnapshot = await getDocs(driversQuery);
      const driversList = driversSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));

      setAvailableDrivers(driversList);
    } catch (error) {
      console.error('Error fetching available drivers:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const calculateBookingCost = () => {
    if (!car || !formData.estimatedDistance) return;

    const distance = parseFloat(formData.estimatedDistance);
    if (isNaN(distance)) return;

    const distanceCharge = distance * car.pricePerKm;
    let driverCharge = 0;

    if (bookingType === 'withDriver' && formData.driverId) {
      // Add driver charge (for example: fixed amount per day or percentage of the total)
      driverCharge = 50; // Sample fixed charge
    }

    const totalAmount = distanceCharge + driverCharge;

    setBookingSummary({
      totalAmount,
      driverCharge,
      distanceCharge
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      setLoading(true);

      if (!currentUser) {
        toast.error('Please login to continue');
        navigate('/login');
        return;
      }

      if (!car) {
        toast.error('Car details not available');
        return;
      }

      if (!formData.customerPhone) {
        toast.error('Please provide your phone number for driver contact');
        return;
      }

      if (bookingType === 'withDriver' && !formData.driverId) {
        toast.error('Please select a driver');
        return;
      }

      // Prepare booking data
      const bookingData = {
        carId: car.id,
        carName: car.name,
        carType: car.type,
        customerId: currentUser.uid,
        customerName: currentUser.displayName,
        customerPhone: formData.customerPhone,
        ...formData,
        bookingType,
        driverId: bookingType === 'withDriver' ? formData.driverId : null,
        status: bookingType === 'withDriver' ? 'assigned' : 'confirmed',
        totalAmount: bookingSummary.totalAmount,
        distanceCharge: bookingSummary.distanceCharge,
        driverCharge: bookingSummary.driverCharge,
        paymentStatus: 'pending',
        createdAt: new Date().toISOString()
      };

      // Add booking to Firestore
      const bookingRef = await addDoc(collection(db, 'bookings'), bookingData);

      toast.success('Booking created successfully!');

      // Navigate to payment page
      navigate(`/payment/${bookingRef.id}`);
    } catch (error) {
      toast.error('Error creating booking: ' + error.message);
      console.error('Error creating booking:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <LoadingSpinner text="Loading car details..." />;
  }

  if (!car) {
    return (
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12 text-center">
        <p className="text-xl text-gray-600">Car not found. Please try another car.</p>
        <button
          onClick={() => navigate('/cars')}
          className="mt-4 bg-primary text-white px-4 py-2 rounded-md hover:bg-primary-dark transition-colors"
        >
          Back to Cars
        </button>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="mb-6">
        <h1 className="text-2xl font-bold mb-2">Book Your Ride</h1>
        <p className="text-gray-600">Complete the form below to book {car.name}</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Car Details */}
        <div className="lg:col-span-1">
          <div className="bg-white rounded-lg shadow-md overflow-hidden">
            <img src={car.imageUrl} alt={car.name} className="w-full h-48 object-cover" />
            <div className="p-4">
              <h2 className="text-xl font-bold mb-1">{car.name}</h2>
              <p className="text-gray-500 mb-2">{car.type}</p>
              <p className="text-primary font-bold mb-3">${car.pricePerKm} per km</p>

              {car.features && car.features.length > 0 && (
                <div className="mb-3">
                  <h3 className="text-sm font-semibold text-gray-600 mb-1">Features:</h3>
                  <ul className="text-sm text-gray-600">
                    {car.features.map((feature, index) => (
                      <li key={index} className="flex items-center">
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 text-green-500 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                        </svg>
                        {feature}
                      </li>
                    ))}
                  </ul>
                </div>
              )}

              <div className="mt-4 bg-gray-50 p-3 rounded-md">
                <h3 className="text-sm font-semibold text-gray-600 mb-2">Booking Summary</h3>
                <div className="text-sm">
                  <div className="flex justify-between mb-1">
                    <span>Distance Charge:</span>
                    <span>${bookingSummary.distanceCharge.toFixed(2)}</span>
                  </div>
                  {bookingType === 'withDriver' && (
                    <div className="flex justify-between mb-1">
                      <span>Driver Charge:</span>
                      <span>${bookingSummary.driverCharge.toFixed(2)}</span>
                    </div>
                  )}
                  <div className="flex justify-between font-bold pt-2 border-t border-gray-200 mt-2">
                    <span>Total Amount:</span>
                    <span>${bookingSummary.totalAmount.toFixed(2)}</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Booking Form */}
        <div className="lg:col-span-2">
          <div className="bg-white rounded-lg shadow-md p-6">
            <form onSubmit={handleSubmit}>
              {/* Booking Type */}
              <div className="mb-6">
                <label className="block text-gray-700 font-medium mb-2">Booking Type</label>
                <div className="flex space-x-4">
                  <label className="flex items-center">
                    <input
                      type="radio"
                      name="bookingType"
                      value="withDriver"
                      checked={bookingType === 'withDriver'}
                      onChange={() => setBookingType('withDriver')}
                      className="h-4 w-4 text-primary focus:ring-primary border-gray-300"
                    />
                    <span className="ml-2">With Driver</span>
                  </label>
                  <label className="flex items-center">
                    <input
                      type="radio"
                      name="bookingType"
                      value="selfDrive"
                      checked={bookingType === 'selfDrive'}
                      onChange={() => setBookingType('selfDrive')}
                      className="h-4 w-4 text-primary focus:ring-primary border-gray-300"
                    />
                    <span className="ml-2">Self Drive</span>
                  </label>
                </div>
              </div>

              {/* Map View */}
              <div className="mb-6">
                <h3 className="text-lg font-medium mb-2">Select Locations on Map</h3>
                <div className="flex space-x-4 mb-3">
                  <button
                    type="button"
                    onClick={() => setLocationMode('pickup')}
                    className={`px-4 py-2 rounded ${
                      locationMode === 'pickup' 
                        ? 'bg-green-500 text-white' 
                        : 'bg-gray-200 text-gray-700'
                    }`}
                  >
                    Set Pickup
                  </button>
                  <button
                    type="button"
                    onClick={() => setLocationMode('drop')}
                    className={`px-4 py-2 rounded ${
                      locationMode === 'drop' 
                        ? 'bg-red-500 text-white' 
                        : 'bg-gray-200 text-gray-700'
                    }`}
                  >
                    Set Drop-off
                  </button>
                </div>
                <div 
                  ref={mapRef} 
                  className="w-full h-64 rounded-lg border border-gray-300"
                >
                  {!mapLoaded && (
                    <div className="h-full flex items-center justify-center bg-gray-100">
                      <p>Loading map...</p>
                    </div>
                  )}
                </div>
                <p className="text-sm text-gray-500 mt-2">
                  Click on the map or use the search fields below to set pickup and drop-off locations
                </p>
              </div>

              {/* Trip Details */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
                <div>
                  <label className="block text-gray-700 font-medium mb-2">Pickup Location</label>
                  <input
                    type="text"
                    id="pickupLocation"
                    name="pickupLocation"
                    value={formData.pickupLocation}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                    placeholder="Enter or select on map"
                    required
                  />
                </div>
                <div>
                  <label className="block text-gray-700 font-medium mb-2">Drop Location</label>
                  <input
                    type="text"
                    id="dropLocation"
                    name="dropLocation"
                    value={formData.dropLocation}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                    placeholder="Enter or select on map"
                    required
                  />
                </div>

                <div>
                  <label className="block text-gray-700 font-medium mb-2">Your Phone Number</label>
                  <input
                    type="tel"
                    name="customerPhone"
                    value={formData.customerPhone}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                    placeholder="For driver to contact you"
                    required
                  />
                </div>

                <div>
                  <label className="block text-gray-700 font-medium mb-2">Estimated Distance (km)</label>
                  <input
                    type="number"
                    name="estimatedDistance"
                    value={formData.estimatedDistance}
                    onChange={handleInputChange}
                    readOnly
                    className="w-full px-3 py-2 border rounded bg-gray-50 focus:outline-none focus:ring-2 focus:ring-primary"
                    required
                  />
                  <p className="text-xs text-gray-500 mt-1">
                    Automatically calculated based on your selected locations
                  </p>
                </div>

                <div>
                  <label className="block text-gray-700 font-medium mb-2">Pickup Date</label>
                  <input
                    type="date"
                    name="pickupDate"
                    value={formData.pickupDate}
                    onChange={handleInputChange}
                    min={new Date().toISOString().split('T')[0]}
                    className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                    required
                  />
                </div>
                <div>
                  <label className="block text-gray-700 font-medium mb-2">Pickup Time</label>
                  <input
                    type="time"
                    name="pickupTime"
                    value={formData.pickupTime}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                    required
                  />
                </div>

                {bookingType === 'selfDrive' && (
                  <>
                    <div>
                      <label className="block text-gray-700 font-medium mb-2">Return Date</label>
                      <input
                        type="date"
                        name="returnDate"
                        value={formData.returnDate}
                        onChange={handleInputChange}
                        min={formData.pickupDate || new Date().toISOString().split('T')[0]}
                        className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                        required={bookingType === 'selfDrive'}
                      />
                    </div>
                    <div>
                      <label className="block text-gray-700 font-medium mb-2">Return Time</label>
                      <input
                        type="time"
                        name="returnTime"
                        value={formData.returnTime}
                        onChange={handleInputChange}
                        className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                        required={bookingType === 'selfDrive'}
                      />
                    </div>
                  </>
                )}

                {bookingType === 'withDriver' && (
                  <div className="md:col-span-2">
                    <label className="block text-gray-700 font-medium mb-2">Select Driver</label>
                    <select
                      name="driverId"
                      value={formData.driverId}
                      onChange={handleInputChange}
                      className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                      required={bookingType === 'withDriver'}
                    >
                      <option value="">Choose a driver</option>
                      {availableDrivers.map(driver => (
                        <option key={driver.id} value={driver.id}>
                          {driver.name} - Rating: {driver.rating || '4.5'}
                        </option>
                      ))}
                    </select>
                  </div>
                )}

                <div className="md:col-span-2">
                  <label className="block text-gray-700 font-medium mb-2">Special Requests (Optional)</label>
                  <textarea
                    name="specialRequests"
                    value={formData.specialRequests}
                    onChange={handleInputChange}
                    rows="3"
                    className="w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-primary"
                    placeholder="Any special requests or instructions for the driver..."
                  ></textarea>
                </div>
              </div>

              <div className="flex justify-end">
                <button
                  type="submit"
                  disabled={loading}
                  className="bg-primary text-white px-6 py-2 rounded font-medium hover:bg-primary-dark transition-colors"
                >
                  {loading ? 'Processing...' : 'Proceed to Payment'}
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BookingPage;
