/**
 * Returns a placeholder image URL for when an image fails to load
 * @param {string} type - Type of placeholder (car, user, etc.)
 * @returns {string} Placeholder image URL
 */
export const getPlaceholderImage = (type = 'car') => {
  switch (type.toLowerCase()) {
    case 'car':
      return 'https://via.placeholder.com/500x300?text=Car+Image';
    case 'user':
    case 'driver':
      return 'https://via.placeholder.com/150x150?text=User';
    default:
      return 'https://via.placeholder.com/400x300?text=Image+Not+Found';
  }
};

/**
 * Image error handler for React components
 * @param {Event} event - The error event
 */
export const handleImageError = (event) => {
  const target = event.currentTarget;
  const fallbackType = target.dataset.type || 'car';
  target.onerror = null; // Prevent infinite loop
  target.src = getPlaceholderImage(fallbackType);
};
